// at45x continius reading datastatic unsigned at45_read_cont(unsigned lba, void *ptr, unsigned len) {    unsigned char req[8];	unsigned addr, page, byte, i;        util_udivmod(lba, AT45_PAGE_SIZE, &page, &byte);	addr = (page << 11) + byte;	util_printf("\nReading page = 0x%x, byte = 0x%x addr = 0x%x, length = 0x%x\n",			page, byte, addr, len);	req[0] = 0xe8;	req[1] = ((addr & 0x00ff0000) >> 16);	req[2] = ((addr & 0x0000ff00) >> 8);	req[3] = ((addr & 0x000000ff));    req[4] = 0x0;    req[5] = 0x0;    req[6] = 0x0;    req[7] = 0x0;    	at45_transfer(req, 8, ptr, len);    	return 0;}// at45x reading datastatic unsigned at45_read(unsigned lba, void *ptr, unsigned len) {	unsigned page, pg_n, z0, z1;    // check data address and size    util_udivmod(lba, AT45_PAGE_SIZE, &page, &z0);    util_udivmod(len, AT45_PAGE_SIZE, &pg_n, &z1);	if (z0 != 0)		return -1;	if (z1 != 0)		return -1;        util_printf("Reading page = 0x%x, count = 0x%x\n",			page, pg_n);        while (pg_n--) {        at45_wait_rdy();        		at45_page_read(lba, ptr);		ptr += AT45_PAGE_SIZE;        lba += AT45_PAGE_SIZE;	}	at45_wait_rdy();    return 0;}